DICTIONARY = dict(zip(STOP_WORDS,STOP_WORDS))
PATH = "C:\Program Files (x86)\chromedriver"
DOCUMENTS_a = []
DOCUMENTS_b = []
PAGES_a = []
PAGES_b = []
NAMES_a = []
NAMES_b = []
NUM_PAG = 100

CSV_a = 'nba_com_links.csv'
#CSV_a = 'rotoworld_links.csv'
CSV_b = 'basketball_links.csv'

csv_b = pd.read_csv(CSV_b)
csv_a = pd.read_csv(CSV_a)

urls_a = csv_a['nbacom']
#urls_a = csv_a['rotoworld']
urls_b = csv_b['basketball']

#METHODS
def clean_document(page,driver):
    raw_page = page.text.replace("\n"," ")
    list_to_filter = raw_page.split(" ") 
    filtered_list = list_filtering(list_to_filter) 
    document = ' '.join([str(elem) for elem in filtered_list])
    driver.close()
    return document

def list_filtering(list_to_filter):
    filtered_list = []
    for word in list_to_filter:
           if word.isalpha() and not word.lower() in DICTIONARY:
                filtered_list.append(word.lower())
    return filtered_list

def make_lists(url):
    title = ""
    raw_page = ""
    driver = webdriver.Chrome(PATH)
    driver.get(url)
    try:
        title = driver.title
        WebDriverWait(driver, 30)
        raw_page = driver.find_element_by_tag_name('body')
    except:
        print('Eccezione')
    return clean_title(title),clean_document(raw_page,driver)

def clean_title(page_title):
    title_list = page_title.split(" ")
    name_list = title_list[:2]
    return ' '.join([str(elem) for elem in name_list])

def get_name_list(documents):
    names = []
    for name in documents:
        names.append(name[0])
    return names

def get_pages_text(documents):
    texts = []
    for name in documents:
        texts.append(name[1])
    return texts

def calculate_precision(similarity_vector,list_a,list_b):
    _list = []
    for elem in similarity_vector:
        _list.append(list_b[elem])
    return accuracy_score(list_a,_list)

def calculate_recall(similarity_vector,list_a,list_b):
    _list = []
    for elem in similarity_vector:
        _list.append(list_b[elem])
    return recall_score(list_a,_list,average='macro')

def calculate_f1(precision,recall):
    return (2*precision*recall)/(precision+recall)

DOCUMENTS_a = Parallel(n_jobs=10,verbose=100)(delayed(make_lists)(url) for url in urls_a[:NUM_PAG])
DOCUMENTS_b = Parallel(n_jobs=10,verbose=100)(delayed(make_lists)(url) for url in urls_b[:NUM_PAG])

PAGES_a = get_pages_text(DOCUMENTS_a)
PAGES_b = get_pages_text(DOCUMENTS_b)
NAMES_a = get_name_list(DOCUMENTS_a)
NAMES_b = get_name_list(DOCUMENTS_b)

#VECTORIZATION
count_vectorizer = TfidfVectorizer(stop_words='english')

smatrix_a = count_vectorizer.fit_transform(PAGES_a)
smatrix_b = count_vectorizer.transform(PAGES_b)

term_matrix_a = smatrix_a.todense()
term_matrix_b = smatrix_b.todense()

data_a = pd.DataFrame(term_matrix_a,columns=count_vectorizer.get_feature_names())
data_b = pd.DataFrame(term_matrix_b,columns=count_vectorizer.get_feature_names())

#METRICS
similarities_matrix = pd.DataFrame(cosine_similarity(data_a, data_b))
similarities = pd.DataFrame(similarities_matrix.idxmax(axis=1),columns=['similarity'])
similarity_vector = list(similarities['similarity'])

#PRECISION, RECALL, F-MEASURE
precision = calculate_precision(similarity_vector,NAMES_a,NAMES_b)
recall = calculate_recall(similarity_vector,NAMES_a,NAMES_b)
f1 = (2* precision * recall)/(precision + recall)

